import styled, { css } from 'styled-components';

import { TYPOGRAPHY } from '../../../Typography';
import type { Size } from '../../common';
import { DEFAULT_THEME } from '../../../common';

interface IPhoneCode {
  size: Size;
  disabled?: boolean;
}

export const PhoneCode = styled.div<IPhoneCode>`
  ${({ size, theme, disabled }) => css`
    display: flex;
    align-items: center;
    padding-left: 16px;
    font-family: ${TYPOGRAPHY.fontFamily};
    font-size: ${size === 'micro' ? TYPOGRAPHY.fontSize[14] : TYPOGRAPHY.fontSize[16]};
    font-weight: ${TYPOGRAPHY.fontWeight.regular};
    user-select: none;
    color: ${disabled ? theme.color.neutral[30] : theme.color.neutral[90]};
  `}
`;

PhoneCode.defaultProps = {
  theme: DEFAULT_THEME,
};
