import styled from 'styled-components';

import { DEFAULT_THEME } from '../../common';
import { Body2Short } from '../../Typography';
import { ADDITIONAL_TEXT_MARGIN_TOP } from '../constants';

type PropsType = {
  error?: boolean;
  focused?: boolean;
  disabled?: boolean;
};

export const AdditionalText = styled(Body2Short)<PropsType>`
  display: flex;
  margin-top: ${ADDITIONAL_TEXT_MARGIN_TOP};
  color: ${({ theme, focused, disabled, error }) => {
    if (disabled) {
      return theme.color.neutral[30];
    }
    if (error) {
      return theme.color.error[60];
    }
    return focused ? theme.color.neutral[90] : theme.color.neutral[50];
  }};
  user-select: none;
`;

AdditionalText.defaultProps = {
  theme: DEFAULT_THEME,
};
