import styled from 'styled-components';

import { DEFAULT_THEME } from '../../common';
import { Body2Short } from '../../Typography';
import { LABEL_MARGIN_BOTTOM } from '../constants';

type PropsType = {
  focused?: boolean;
  disabled?: boolean;
};

export const Label = styled(Body2Short)<PropsType>`
  display: flex;
  margin-bottom: ${LABEL_MARGIN_BOTTOM};
  color: ${({ theme, focused, disabled }) => {
    if (disabled) {
      return theme.color.neutral[30];
    }
    return focused ? theme.color.neutral[90] : theme.color.neutral[50];
  }};
  user-select: none;
`;

Label.defaultProps = {
  theme: DEFAULT_THEME,
};
