import styled, { css } from 'styled-components';

import { DEFAULT_THEME } from '../../common';
import {
  OPTION_HEIGHT,
  PRODUCT_OPTION_HEIGHT,
  OPTION_PADDING,
  PRODUCT_OPTION_PADDING,
} from '../constants';
import type { Size, SelectType } from '../constants';

type PropsType = {
  size: Size;
  $type: SelectType;
};

export const NoOptionMessage = styled.div<PropsType>`
  display: flex;
  align-items: center;
  ${({ theme, $type, size }) => css`
    height: ${$type === 'default' ? OPTION_HEIGHT[size] : PRODUCT_OPTION_HEIGHT};
    padding: ${$type === 'default' ? OPTION_PADDING[size] : PRODUCT_OPTION_PADDING};
    cursor: default;
    background-color: ${theme.color.neutral.white};
    color: ${theme.color.neutral[50]};
  `}
`;

NoOptionMessage.defaultProps = {
  theme: DEFAULT_THEME,
};
