import styled from 'styled-components';

import { DEFAULT_THEME } from '../../common';

type PropsType = {
  focused?: boolean;
  disabled?: boolean;
};

export const Placeholder = styled.div<PropsType>`
  display: inline-flex;
  align-items: center;
  position: absolute;
  top: 50%;
  transform: translateY(-50%);
  max-width: 100%;
  overflow: hidden;
  white-space: nowrap;
  pointer-events: none;
  color: ${({ theme, focused, disabled }) =>
    focused || disabled ? theme.color.neutral[30] : theme.color.neutral[50]};
  transition: all 0.2s;
  z-index: 4;
`;

Placeholder.defaultProps = {
  theme: DEFAULT_THEME,
};
