import styled from 'styled-components';

import { DEFAULT_THEME } from '../../common';

type PropsType = {
  disabled?: boolean;
};

export const Value = styled.div<PropsType>`
  display: flex;
  align-items: center;
  position: relative;
  text-overflow: ellipsis;
  white-space: nowrap;
  overflow: hidden;
  color: ${({ disabled, theme }) => (disabled ? theme.color.neutral[30] : theme.color.neutral[90])};

  & > div {
    overflow: hidden;
  }
`;

Value.defaultProps = {
  theme: DEFAULT_THEME,
};
