import React from 'react';
import { shallow, mount } from 'enzyme';

import { Tooltip } from '../TooltipHOC/Tooltip';
import { SelectItem } from '../SelectItem';
import { ReactComponent as PersonOutlineIcon } from '../Icons/system/PersonOutlineIcon.svg';

describe('SelectItem', () => {
  beforeEach(() => {
    jest.clearAllMocks();
  });

  it('should render component with icon and text', () => {
    const wrapper = shallow(
      <SelectItem value={1}>
        <PersonOutlineIcon />
        text
      </SelectItem>
    );
    expect(wrapper).toMatchSnapshot();
  });

  it('should render component with correct icon', () => {
    const wrapper = shallow(
      <SelectItem value={1}>
        <PersonOutlineIcon />
        text
      </SelectItem>
    );
    expect(wrapper.childAt(0).is(PersonOutlineIcon));
  });

  it('should render component with disabled state', () => {
    const wrapper = shallow(
      <SelectItem value={1} disabled>
        text
      </SelectItem>
    );
    expect(wrapper).toMatchSnapshot();
  });

  it('should render component with small size', () => {
    const wrapper = shallow(
      <SelectItem value={1} size="small">
        text
      </SelectItem>
    );
    expect(wrapper).toMatchSnapshot();
  });

  it('should render component with tooltip', () => {
    const wrapper = mount(
      <SelectItem value={1}>
        texttttttttttttttttttttttttttttttttttttttttttttttttttttttttttttttt
      </SelectItem>
    );
    expect(wrapper.find(Tooltip));
  });
});
