import * as React from 'react';

export interface AvatarProps extends React.SVGProps<SVGSVGElement> {
  /** URL аватарки пользователя */
  href?: string;
}

export const Avatar = ({ href, ...props }: AvatarProps) => {
  return (
    <svg
      width="56"
      height="56"
      xmlns="http://www.w3.org/2000/svg"
      viewBox="0 0 50 50"
      {...props}
      style={{ transform: 'rotate(.00001deg)' }} /* hack to force anti-aliasing in IE */
    >
      <defs>
        <mask id="shape-mask">
          <circle id="outer" cx="25" cy="25" r="25" strokeWidth="0" stroke="white" fill="white" />
          <ellipse
            id="inner"
            vectorEffect="non-scaling-stroke"
            cx="42"
            cy="42"
            rx="8"
            ry="8"
            strokeWidth="2"
            fill="black"
            stroke="black"
          />
        </mask>
      </defs>
      {href && (
        <image
          width="100%"
          height="100%"
          preserveAspectRatio="xMidYMid slice"
          mask="url(#shape-mask)"
          xlinkHref={href}
        />
      )}

      <ellipse
        style={{ fill: '#2ABB5B', strokeWidth: 0, strokeOpacity: 0 }}
        vectorEffect="non-scaling-stroke"
        cx="42"
        cy="42"
        rx="8"
        ry="8"
      />
    </svg>
  );
};
