import type { FC, MouseEvent } from 'react';
import React, { memo } from 'react';
import { ReactComponent as ChevronLeftOutline } from '@openvtb/admiral-icons/build/system/ChevronLeftOutline.svg';
import { ReactComponent as ChevronRightOutline } from '@openvtb/admiral-icons/build/system/ChevronRightOutline.svg';

import { PanelButton } from '../styled/PanelButton';

type ButtonProps = {
  onMouseDown: (event: MouseEvent<HTMLDivElement>) => void;
  disabled: boolean;
  type: 'left' | 'right';
};

const ICON_SIZE = 24;

const ButtonComponent: FC<ButtonProps> = ({ onMouseDown, disabled, type }) => (
  <PanelButton
    onMouseDown={(event) => {
      event?.preventDefault();
      onMouseDown(event);
    }}
    disabled={disabled}
  >
    {type === 'left' && <ChevronLeftOutline width={ICON_SIZE} height={ICON_SIZE} />}
    {type === 'right' && <ChevronRightOutline width={ICON_SIZE} height={ICON_SIZE} />}
  </PanelButton>
);

export const Button = memo<ButtonProps>(ButtonComponent);
