import React from 'react';
import styled from 'styled-components';
import { ReactComponent as LogoVTB } from './logo-VTB.svg';

const direction = (vertical: boolean) => (vertical ? 'height' : 'width');

const up = (breakpoint: string, vertical = false) =>
  `@media (min-${direction(vertical)}: calc(${breakpoint} + 0.02px))`;

const down = (breakpoint: string, vertical = false) => `@media (max-${direction(vertical)}: ${breakpoint})`;

const between = (breakpointMin: string, breakpointMax: string, vertical = false) =>
  `@media (max-${direction(vertical)}: ${breakpointMax}) and (min-${direction(
    vertical,
  )}: calc(${breakpointMin} + 0.02px))`;

const Container = styled.div`
  display: flex;
  background-color: #002db2;
`;

const Logo = styled(LogoVTB)``;

export function Header(props: React.HTMLAttributes<HTMLDivElement>) {
  return (
    <Container {...props}>
      <Logo />
    </Container>
  );
}
