import * as React from 'react';
import type { InputStatus } from '~/components';
import { ReactComponent as ErrorStatusSvg } from '@openvtb/admiral-icons/build/service/ErrorSolid.svg';
import { ReactComponent as SuccessStatusSvg } from '@openvtb/admiral-icons/build/service/CheckSolid.svg';
import styled from 'styled-components';

const ErrorIcon = styled(ErrorStatusSvg)`
  fill: ${(props) => props.theme.color.status.danger};
`;

const SuccessIcon = styled(SuccessStatusSvg)`
  fill: ${(props) => props.theme.color.status.success};
`;

export interface StatusIconProps {
  status?: InputStatus;
}

export function StatusIcon({ status, ...props }: StatusIconProps) {
  switch (status) {
    case 'error':
      return <ErrorIcon {...props} />;

    default:
      return <SuccessIcon {...props} />;
  }
}
