import React from 'react';

import { BadgeStyled, NotificationIconStyled, NotificationIconWrapper } from './styled';

type PropsType = {
    value: number
    onClick: () => void
}

export const NotificationIcon: React.FC<PropsType> = props => {
    const { value, onClick } = props;
    return (
        <NotificationIconWrapper onClick={onClick}>
            <NotificationIconStyled/>
            <BadgeStyled dimension="s" appearance="error">{value}</BadgeStyled>
        </NotificationIconWrapper>
    );
};