import { MouseEvent } from 'react';

export enum NotificationType {
    DANGER = 'DANGER',
    INFO = 'INFO',
    SUCCESS = 'SUCCESS',
    WARNING = 'WARNING'
}

export type NotificationAlert = {
    id: number | string,
    type?: NotificationType
    date?: string
    title?: string
    duration?: number
    message?: string
    locked?: boolean
    closable?: boolean
    memorable?: boolean
    notifiable?:boolean
}

export type NotificationAlertExtended = NotificationAlert & {
    alert: boolean
    new: boolean
    onClose?: (id: any) => void,
    onClick?: (event: MouseEvent<HTMLDivElement>, id?: any) => void,
}