import { forIn } from 'lodash';

export const deepOverrideFunctionToString = (obj: object, newFunction: () => string) => {
    forIn(obj, (item: any, key: string) => {
        if (key !== 'params') {
            item.toString = newFunction;
            if (typeof item === 'object') deepOverrideFunctionToString(item, newFunction);
        }
    });
};